// src/App.js
import React, { useState } from 'react';
import { TimerClassComponent } from './TimerClassComponent';     // Import Class Component
import './App.css'; // Giữ lại nếu bạn đang sử dụng CSS này
// import './index.css'; // Thường thì index.css được import trong index.js, không cần ở đây

export default function App() {
  const [functionInitialDelay, setFunctionInitialDelay] = useState(0);
  const [classInitialDelay, setClassInitialDelay] = useState(0);
  const [showComponents, setShowComponents] = useState(true);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-8 space-y-12">
      <h1 className="text-4xl font-bold text-gray-900 mb-8">So sánh useEffect và Class Component Lifecycle</h1>

      <div className="flex space-x-6 mb-8">
        <button
          onClick={() => setShowComponents(!showComponents)}
          className="px-6 py-3 bg-indigo-500 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-600 focus:outline-none focus:ring-2 focus:ring-indigo-400 focus:ring-opacity-75 transition duration-300 ease-in-out transform hover:scale-105"
        >
          {showComponents ? 'Ẩn Components' : 'Hiển thị Components'}
        </button>
      </div>

      {showComponents && (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-12 w-full max-w-4xl">
          {/* Function Component */}
          <div className="flex flex-col items-center space-y-4">
            <h2 className="text-3xl font-semibold text-purple-700">Function Component</h2>
            <input
              type="number"
              value={functionInitialDelay}
              onChange={(e) => setFunctionInitialDelay(parseInt(e.target.value))}
              className="mt-2 p-2 border border-gray-300 rounded-md text-center text-lg"
              placeholder="Set initial delay"
            />
            <TimerFunctionComponent initialDelay={functionInitialDelay} />
          </div>

          {/* Class Component */}
          <div className="flex flex-col items-center space-y-4">
            <h2 className="text-3xl font-semibold text-green-700">Class Component</h2>
            <input
              type="number"
              value={classInitialDelay}
              onChange={(e) => setClassInitialDelay(parseInt(e.target.value))}
              className="mt-2 p-2 border border-gray-300 rounded-md text-center text-lg"
              placeholder="Set initial delay"
            />
            <TimerClassComponent initialDelay={classInitialDelay} />
          </div>
        </div>
      )}
    </div>
  );
}
