import React from 'react';

// Tạo một Class Component có tên là TimerClassComponent
export class TimerClassComponent extends React.Component {
  // Constructor là nơi khởi tạo state và liên kết các phương thức nếu cần
  constructor(props) {
    super(props); // Luôn gọi super(props)
    this.state = {
      seconds: 0, // State để lưu trữ số giây đã trôi qua
    };
    this.intervalId = null; // Biến để lưu ID của setInterval, dùng cho việc dọn dẹp
    console.log('[Class Component] Constructor: Component được khởi tạo.');
  }

  // componentDidMount được gọi ngay sau khi component được gắn vào DOM (render lần đầu)
  // Đây là nơi lý tưởng để bắt đầu các side effects như thiết lập timer, fetch data
  componentDidMount() {
    console.log('[Class Component] componentDidMount: Component đã được gắn vào DOM. Bắt đầu bộ đếm.');
    this.startTimer(); // Gọi phương thức để bắt đầu bộ đếm
  }

  // componentDidUpdate được gọi sau mỗi lần component được cập nhật (re-render)
  // (trừ lần render đầu tiên sau componentDidMount)
  // Không cần trong ví dụ đồng hồ đơn giản này vì nó không phụ thuộc vào props thay đổi
  // Tuy nhiên, nếu bạn muốn reset đồng hồ khi một prop nào đó thay đổi, bạn sẽ dùng nó.
  // Trong ví dụ này, chúng ta không cần nó cho chức năng đếm thời gian cơ bản.
  /*
  componentDidUpdate(prevProps, prevState) {
    // Ví dụ: Nếu bạn muốn reset đồng hồ khi một prop 'resetKey' thay đổi
    // if (this.props.resetKey !== prevProps.resetKey) {
    //   this.stopTimer();
    //   this.setState({ seconds: 0 });
    //   this.startTimer();
    // }
    console.log('[Class Component] componentDidUpdate: Component đã cập nhật.');
  }
  */

  // componentWillUnmount được gọi ngay trước khi component bị gỡ khỏi DOM
  // Đây là nơi quan trọng để dọn dẹp mọi side effects đã thiết lập,
  // như xóa timer, hủy bỏ các subscription để tránh rò rỉ bộ nhớ
  componentWillUnmount() {
    console.log('[Class Component] componentWillUnmount: Component sắp bị gỡ khỏi DOM. Dọn dẹp bộ đếm.');
    this.stopTimer(); // Gọi phương thức để dừng và dọn dẹp bộ đếm
  }

  // Phương thức để bắt đầu bộ đếm thời gian
  startTimer = () => {
    // Đảm bảo chỉ có một interval chạy cùng lúc
    if (!this.intervalId) {
      this.intervalId = setInterval(() => {
        // Cập nhật state `seconds` mỗi giây
        this.setState(prevState => ({
          seconds: prevState.seconds + 1,
        }));
      }, 1000); // 1000ms = 1 giây
    }
  };

  // Phương thức để dừng bộ đếm thời gian và xóa interval
  stopTimer = () => {
    if (this.intervalId) {
      clearInterval(this.intervalId); // Hủy bỏ interval
      this.intervalId = null; // Đặt lại về null
    }
  };

  // Phương thức render chịu trách nhiệm hiển thị UI của component
  render() {
    console.log('[Class Component] Render: Hiển thị giao diện.');
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
        <div className="bg-white p-8 rounded-lg shadow-xl flex flex-col items-center space-y-6">
          <h2 className="text-2xl font-bold text-gray-800">Đồng hồ đếm (Class Component)</h2>
          <p className="text-6xl font-extrabold text-blue-600 font-inter">
            {this.state.seconds}
          </p>
          <p className="text-xl text-gray-700 font-semibold">
            Giây đã trôi qua
          </p>
        </div>
      </div>
    );
  }
}

// Bạn có thể import component này vào App.js để sử dụng
// Ví dụ: import { TimerClassComponent } from './TimerClassComponent';
