import React from 'react';

export class TimerClassComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      seconds: props.initialDelay || 0, // Sử dụng prop initialDelay
    };
    this.intervalId = null; // Khởi tạo biến để lưu ID của interval
    console.log('[Class] Constructor: Khởi tạo state và biến intervalId.');
  }

  // Được gọi sau khi component được render lần đầu tiên vào DOM
  componentDidMount() {
    console.log(`[Class] componentDidMount: Component đã mount. Bắt đầu bộ đếm từ ${this.state.seconds}.`);
    this.startTimer();
  }

  // Được gọi sau mỗi lần render lại (trừ lần render đầu tiên)
  componentDidUpdate(prevProps, prevState) {
    // So sánh prop `initialDelay` hiện tại với prop `initialDelay` trước đó
    if (this.props.initialDelay !== prevProps.initialDelay) {
      console.log(`[Class] componentDidUpdate: initialDelay thay đổi từ ${prevProps.initialDelay} sang ${this.props.initialDelay}.`);
      this.stopTimer(); // Dừng bộ đếm cũ
      this.setState({ seconds: this.props.initialDelay }); // Đặt lại số giây
      this.startTimer(); // Bắt đầu bộ đếm mới
    }
  }

  // Được gọi ngay trước khi component bị gỡ khỏi DOM
  componentWillUnmount() {
    console.log('[Class] componentWillUnmount: Dọn dẹp bộ đếm thời gian.');
    this.stopTimer(); // Dừng bộ đếm khi component bị unmount
  }

  startTimer = () => {
    // Chỉ khởi tạo interval nếu nó chưa tồn tại (để tránh nhiều interval chạy cùng lúc)
    if (!this.intervalId) {
      this.intervalId = setInterval(() => {
        this.setState(prevState => ({
          seconds: prevState.seconds + 1,
        }));
      }, 1000);
    }
  };

  stopTimer = () => {
    if (this.intervalId) {
      clearInterval(this.intervalId);
      this.intervalId = null; // Đặt lại về null để có thể khởi tạo lại
    }
  };

  render() {
    console.log(`[Class] Render: Hiển thị ${this.state.seconds} giây.`);
    return (
      <div className="bg-white p-8 rounded-lg shadow-xl flex flex-col items-center space-y-6">
        <h2 className="text-2xl font-bold text-gray-800">Đồng hồ đếm (Class Component)</h2>
        <p className="text-6xl font-extrabold text-blue-600 font-inter">
          {this.state.seconds}
        </p>
        <p className="text-xl text-gray-700 font-semibold">
          Giây đã trôi qua (Bắt đầu từ {this.props.initialDelay})
        </p>
      </div>
    );
  }
}
